// src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, useParams } from 'react-router-dom';

// Import your images
import defaultImage from './images/default-image.jpg';
import anotherImage from './images/another-image.jpg';

// Mock API function to simulate fetching a message
const fetchDefaultMessage = (name) => {
  return new Promise((resolve) => {
    setTimeout(() => {
      resolve(`May this Diwali bring you endless joy, ${name}!`);
    }, 1000); // Simulate a delay
  });
};

const Home = () => {
  return (
    <div style={styles.container}>
      <h1>Welcome to Diwali Wishes!</h1>
      <p>Change the URL to /wishes/YourName to see your personalized wishes!</p>
    </div>
  );
};

const Wishes = () => {
  const { name } = useParams();
  const [backgroundImage, setBackgroundImage] = useState(defaultImage); // Use the imported default image
  const [customMessage, setCustomMessage] = useState(`May this festival of lights bring you joy, peace, and prosperity!`);
  const [loading, setLoading] = useState(false);

  const changeBackground = () => {
    const newImage = backgroundImage === defaultImage ? anotherImage : defaultImage; // Toggle image
    setBackgroundImage(newImage);
  };

  const getDefaultMessage = async () => {
    setLoading(true);
    const message = await fetchDefaultMessage(name);
    setCustomMessage(message);
    setLoading(false);
  };

  return (
    <div style={{ ...styles.container, backgroundImage: `url(${backgroundImage})`, backgroundSize: 'cover', backgroundPosition: 'center' }}>
      <h1 style={styles.text}>Happy Diwali, {name}!</h1>
      <p style={styles.text}>{customMessage}</p>
      <button onClick={changeBackground} style={styles.button}>Change Background</button>
      <button onClick={getDefaultMessage} style={styles.button}>Get Default Message</button>
      {loading && <p style={styles.loading}>Loading...</p>}
    </div>
  );
};

const App = () => {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/wishes/:name" element={<Wishes />} />
      </Routes>
    </Router>
  );
};

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    height: '100vh',
    textAlign: 'center',
    color: 'white', // Ensuring text is readable against backgrounds
    position: 'relative',
  },
  text: {
    margin: '10px 0',
  },
  button: {
    padding: '10px',
    borderRadius: '5px',
    border: 'none',
    backgroundColor: '#4CAF50',
    color: 'white',
    cursor: 'pointer',
    marginTop: '20px',
  },
  loading: {
    marginTop: '20px',
    fontStyle: 'italic',
  },
};

export default App;
