// src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, useParams } from 'react-router-dom';

// Import your images
import defaultImage from './images/default-image.jpg';
import anotherImage from './images/another-image.jpg';
import thirdImage from './images/third-image.jpg'; // Add more images as needed

const backgroundImages = [
  { src: defaultImage, label: "Default Background" },
  { src: anotherImage, label: "Another Background" },
  { src: thirdImage, label: "Third Background" },
];

const defaultMessages = [
  "May this festival of lights bring you joy, peace, and prosperity!",
  "Wishing you a joyous Diwali filled with happiness and wealth!",
  "May the light of Diwali fill your home with joy and warmth!",
  "Wishing you and your family a blessed Diwali!",
  "May this Diwali bring new happiness, new goals, and new achievements!",
];

const getRandomMessage = (name) => {
  const randomIndex = Math.floor(Math.random() * defaultMessages.length);
  return `${defaultMessages[randomIndex]} - ${name}`;
};

const Home = () => {
  return (
    <div style={styles.container}>
      <h1>Welcome to Diwali Wishes!</h1>
      <p>Change the URL to /wishes/YourName to see your personalized wishes!</p>
    </div>
  );
};

const Wishes = () => {
  const { name } = useParams();
  const [currentBackgroundIndex, setCurrentBackgroundIndex] = useState(0);
  const [customMessage, setCustomMessage] = useState(getRandomMessage(name));

  const changeBackgroundForward = () => {
    setCurrentBackgroundIndex((currentBackgroundIndex + 1) % backgroundImages.length);
  };

  const changeBackgroundBackward = () => {
    setCurrentBackgroundIndex((currentBackgroundIndex - 1 + backgroundImages.length) % backgroundImages.length);
  };

  const getDefaultMessage = () => {
    setCustomMessage(getRandomMessage(name));
  };

  return (
    <div style={{ 
      ...styles.container, 
      backgroundImage: `url(${backgroundImages[currentBackgroundIndex].src})`, 
      backgroundSize: 'cover', 
      backgroundPosition: 'center' 
    }}>
      <h1 style={styles.text}>Happy Diwali, {name}!</h1>
      <p style={styles.text}>{customMessage}</p>
      <div style={styles.buttonContainer}>
        <button onClick={changeBackgroundBackward} style={styles.button}>&lt; Prev</button>
        <button onClick={changeBackgroundForward} style={styles.button}>Next &gt;</button>
        <button onClick={getDefaultMessage} style={styles.button}>Get New Message</button>
      </div>
    </div>
  );
};

const App = () => {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/wishes/:name" element={<Wishes />} />
      </Routes>
    </Router>
  );
};

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    height: '100vh',
    textAlign: 'center',
    color: 'white',
    position: 'relative',
  },
  text: {
    margin: '10px 0',
  },
  button: {
    padding: '10px',
    borderRadius: '5px',
    border: 'none',
    backgroundColor: '#4CAF50',
    color: 'white',
    cursor: 'pointer',
    margin: '5px',
  },
  buttonContainer: {
    position: 'absolute',
    right: '20px',
    top: '20px',
    display: 'flex',
    flexDirection: 'column',
  },
};

export default App;
