// src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, useParams } from 'react-router-dom';

// Import your images
import defaultImage from './images/Diwali2.gif';
import anotherImage from './images/diwali1.jpg';

const defaultMessages = [
  "May this festival of lights bring you joy, peace, and prosperity!",
  "Wishing you a joyous Diwali filled with happiness and wealth!",
  "May the light of Diwali fill your home with joy and warmth!",
  "Wishing you and your family a blessed Diwali!",
  "May this Diwali bring new happiness, new goals, and new achievements!",
  "May the divine light of Diwali bring peace, prosperity, and happiness to your life Shine on and have a joyous Diwali.",

];

const getRandomMessage = (name) => {
  const randomIndex = Math.floor(Math.random() * defaultMessages.length);
  return `${defaultMessages[randomIndex]} - ${name}`;
};

const Home = () => {
  return (
    <div style={styles.container}>
      <h1>Welcome to Diwali Wishes!</h1>
      <p>Change the URL to /wishes/YourName to see your personalized wishes!</p>
    </div>
  );
};

const Wishes = () => {
  const { name } = useParams();
  const [backgroundImage, setBackgroundImage] = useState(defaultImage);
  const [customMessage, setCustomMessage] = useState(getRandomMessage(name));

  const changeBackground = () => {
    const newImage = backgroundImage === defaultImage ? anotherImage : defaultImage;
    setBackgroundImage(newImage);
  };

  const getDefaultMessage = () => {
    setCustomMessage(getRandomMessage(name));
  };

  return (
    <div style={{ ...styles.container, backgroundImage: `url(${backgroundImage})`, backgroundSize: 'cover', backgroundPosition: 'center' }}>
      <h1 style={styles.text}>Happy Diwali, {name}!</h1>
      <p style={styles.text}>{customMessage}</p>
      <button onClick={changeBackground} style={styles.button}>Change Background</button>
      <button onClick={getDefaultMessage} style={styles.button}>Get New Message</button>
    </div>
  );
};

const App = () => {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/wishes/:name" element={<Wishes />} />
      </Routes>
    </Router>
  );
};

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    height: '100vh',
    textAlign: 'center',
    color: 'white',
    position: 'relative',
  },
  text: {
    margin: '10px 0',
  },
  button: {
    padding: '10px',
    borderRadius: '5px',
    border: 'none',
    backgroundColor: '#4CAF50',
    color: 'white',
    cursor: 'pointer',
    marginTop: '20px',
  },
};

export default App;
